create table news_item (id bigint generated by default as identity, created_date timestamp, last_modified_date timestamp, image_url varchar(255), link varchar(255), summary varchar(255), tag varchar(255), title varchar(255), primary key (id));
create table player (id bigint generated by default as identity, created_date timestamp, last_modified_date timestamp, current_handicap integer not null, first_name varchar(255), surname varchar(255), primary key (id));
create table race_to_ciao_bella (id bigint generated by default as identity, created_date timestamp, last_modified_date timestamp, season integer not null, primary key (id));
create table result (id bigint generated by default as identity, created_date timestamp, last_modified_date timestamp, handicap integer not null, score integer not null, player_id bigint, tournament_id bigint, primary key (id));
create table tournament (id bigint generated by default as identity, created_date timestamp, last_modified_date timestamp, date timestamp, name varchar(255), race_to_ciao_bella_id bigint, venue_id bigint, primary key (id));
create table venue (id bigint generated by default as identity, created_date timestamp, last_modified_date timestamp, google_map_link varchar(255), image_url varchar(255), link varchar(255), name varchar(255), primary key (id));
alter table result add constraint FK_gafn70ioalgac95sc3fpfgovm foreign key (player_id) references player;
alter table result add constraint FK_ex3sq5mqo01hfs9yfnyn7dv4c foreign key (tournament_id) references tournament;
alter table tournament add constraint FK_6qrx1r0e9ckwk5wgk48pen61o foreign key (race_to_ciao_bella_id) references race_to_ciao_bella;
alter table tournament add constraint FK_kv3xogav1d7lxj9ovjmk7s3j0 foreign key (venue_id) references venue;